
public class Tarea1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 5;
		
		int suma = a+b;
		int resta = a-b;
		int mult = a*b;
		int div = a/b;
		int modulo = a%b;
		
		System.out.println(suma);
		System.out.println(resta);
		System.out.println(mult);
		System.out.println(div);
		System.out.println(modulo);
		
	}

}

